        if(pRef.current.className==="hide"){
            pRef.current.className = "show";
        }else{
            pRef.current.className = "hide";
        }

        console.log(pRef.className);
    }
    return (
        <div>
            <div className="product-card">
                <p onClick={printTitle}>{title}</p>
                <p ref={pRef} className="hide">
                    {price}
                </p>
