import "./ProductCard.css"
import { useRef, useState} from "react";

function ProductCard({title,price}){
    let pRef = useRef(0);
    let inputRef=useRef(0);
    let titleRef=useRef(0);
    let [inputV, setInputV]=use
    function printTitle(){
        console.log("Price show");
        if(pRef.current.className==="hide"){
            pRef.current.className = "show";
        }else{
            pRef.current.className = "hide";
        }

        console.log(pRef.className);
    }
    function logInput(){
        titleRef.current.innerText= inputRef.current.valueOf;
    }
    return (
        <div>
            <div className="product-card">
                <input ref={inputRef} onClick={logInput} />
                <p onClick={printTitle} ref={titleRef}>
                    {title}
                </p>
                <p ref={pRef} className="hide">
                    {price}
                </p>
            </div>
        </div>
    );
}
export default ProductCard;
